import {React, useState, useEffect} from 'react';
import './App.css';
import CreateGame from './CreateGame';
import GameInfo from './GameInfo';
import Header from './Header';

function App() {

  const [blam,setBlam] = useState([]);
  const [hideCreate,setHideCreate] = useState(true)

  useEffect(()=>{
    fetch('http://localhost:9292/games')
    .then(res => res.json())
    .then(data => {
      console.log(data); setBlam(data)
})},[]
)

const handleCreate = (e)=>{
  e.preventDefault()
  setHideCreate(!hideCreate);
}

let games = blam.map((e)=>{
  return (
    <GameInfo 
    title = {e.title}
    coverArt = {e.img_url}
    gameYear = {e.year}
    review = {e.review}
    id = {e.id}
    />
  )
})

console.log(blam)

  return (
    <div className="App">
      <div id='createContainer'> 
        <button id ="createBtn"onClick={handleCreate}>Create New Review!</button>
      </div>
    <Header />
    <CreateGame hideCreate={hideCreate} />
      {games} 
    </div>
  );
}

export default App;
