import {React, useState} from "react"


function GameInfo({
    title, coverArt, gameYear,review
}){

    const [hidden, useHidden] = useState(true);

    const handleUpdate=()=>useHidden(!hidden)  


    return(
        <div id="gameInfo">
            <img id="coverArt" src={coverArt} alt="This didn't load for some reason"></img>            
            <h1 id="gameTitle">{title}</h1>
            <h2 id="gameYear">Release Year: {gameYear}</h2>
            <h2 id="review">{review}/10</h2>
            <div id="update">
                <input type="text" hidden={hidden}></input>
            </div>
            <div id="buttons">
                <button id="updateGame" onClick={handleUpdate}>Update?</button>
                <button id="deleteGame">Delete?</button>
            </div>
        </div>
    )

};

export default GameInfo
