  return (
    <GameInfo 
    title = {e.title}
    coverArt = {e.img_url}
    gameYear = {e.year}
    review = {e.review}
    id = {e.id}
    render={render} setRender={setRender}
    />
  )
})

console.log(blam)

  return (
    <div className="App">
    <Header />
    <CreateGame render={render} setRender={setRender}/>
      {games} 
    </div>
  );
}

