import {React, useState} from "react"


function GameInfo({
    title, coverArt, gameYear,review
}){

    const [hidden, setHidden] = useState(false);
    const [patch, setPatch] = useState();

    function handleNumber(e){
        console.log(e.target.value);
        setPatch(e.target.value);
        console.log(patch)
    }  

    return(
        <div id="gameInfo">
            <img id="coverArt" src={coverArt} alt="This didn't load for some reason"></img>            
            <h1 id="gameTitle">{title}</h1>
            <h2 id="gameYear">Release Year: {gameYear}</h2>
            <h2 id="review">{review}/10</h2>
            <div id="buttons">
                <button id="updateGame" onClick={()=>setHidden(!hidden)}>Update?</button>
                <button id="deleteGame">Delete?</button>
            </div>
            <div id="update">
                <select id="textUpdate" onChange={handleNumber} type="text" hidden={hidden} placeholder="Update rating here!">
                    <option value=""></option>
                    <option value="0">0</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="6">6</option>
                    <option value="7">7</option>
                    <option value="8">8</option>
                    <option value="9">9</option>
                    <option value="10">10</option>                    
                </select>
            </div>
        </div>
    )

};

export default GameInfo
